index n of ThirdPartyComponents.Import;

if ThirdPartyComponents.Import(n).StartImport
then 
local string connectionfile = GetSetting("Data.XML.ConnectionString") + ThirdPartyComponents.Import(n).FileName + ".xml";
ThirdPartyComponents.Import(n).Items@options = GetOptions("ThirdPartyComponents.Import[" & n & "].Items"; true; Connectionfile; "ITEMS");
ThirdPartyComponents.Import(n).StartImport = false;
end if;

index n of ThirdPartyComponents.Import;

ThirdPartyComponents.Import(n).Item@length = Len(ThirdPartyComponents.Import(n).Items@options);

Index i of ThirdPartyComponents.Import.Item;
ThirdPartyComponents.Import(n).Item(i)@repeat = false;
ThirdPartyComponents.Import(n).Item(i).ItemId = ThirdPartyComponents.Import(n).Items@option(i)@id;
ThirdPartyComponents.Import(n).Item(i).Name = ThirdPartyComponents.Import(n).Items@option(i)@label;
local string connectionfile = GetSetting("Data.XML.ConnectionString") + ThirdPartyComponents.Import(n).FileName + ".xml";
ThirdPartyComponents.Import(n).Item(i).Price = GetDoubleValue(connectionfile; "ITEMS"; "SalesPrice"; "ItemId"; "="; ThirdPartyComponents.Import(n).Item(i).ItemId) 
end index;

